INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT, UI_CLASS) VALUES (20079, 0, 20078, 2, 2003, 'text', '4a. If YES, what types of research?', 'depends-on depends-on-Yes-20078');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT, UI_CLASS) VALUES (20080, 0, 20078, 2, 2003, 'text', '4b. If YES, why might you have NOT wanted your tissue or blood used for that type of research?', 'depends-on depends-on-Yes-20078');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20081, 0, '', 2, 2004, 'scale7', '1. Researchers conduct important research that helps to prevent, diagnose and treat serious diseases.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20082, 0, '', 2, 2004, 'scale7', '2. Research does not lead to real improvements in diagnosis and treatment that help patients.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20083, 0, '', 2, 2004, 'scale7', '3. Researchers can be trusted to keep the identity secret of people who participate in research.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20084, 0, '', 2, 2004, 'scale7', '4. Doctors who do medical research do not care much about what is best for each individual patient.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20085, 0, '', 2, 2004, 'scale7', '5. Doctors tell their patients everything they need to know about being in a research study.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20086, 0, '', 2, 2004, 'scale7', '6. Medical researchers treat people with very little respect and dignity.');
INSERT INTO "CDRDS"."SV_QUESTION" (ID, VERSION, PARENT_QUESTION_ID, SURVEY_TEMPLATE_ID, SURVEY_SECTION_ID, ANSWER_TYPE, QUESTION_TEXT) VALUES (20087, 0, '', 2, 2004, 'scale7', '7. I trust doctors a lot who do medical research.');
